const anchors=document.querySelectorAll('a[href*="#"]');for(let e of anchors)e.addEventListener("click",(function(t){t.preventDefault();const r=e.getAttribute("href").substr(1);document.getElementById(r).scrollIntoView({behavior:"smooth",block:"start"})}));const learnMoreButtons=document.querySelectorAll(".services-container-block-item__wrapper");for(let e of learnMoreButtons)e.querySelector("button").addEventListener("click",showInfo);function showInfo(e){console.log(),e.target.innerText=="learn more".toUpperCase()?(e.target.innerText="close".toUpperCase(),e.target.parentElement.querySelector(".info").classList.add("active")):(e.target.innerText="learn more".toUpperCase(),e.target.parentElement.querySelector(".info").classList.remove("active"))}